import React from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { playAudio } from "../../actions/profile";

const ProfileAudio = ({ audio, playAudio }) => (
  <div id="music-container" class="music-container">
    <tr>
      <td>{audio.title}</td>
      <td>
        <button id="play" onClick={() => playAudio()} className="btn btn-dark">
          <i class="fas fa-play"></i>
        </button>
        <audio id="audio" src={`/api/upload/${audio.fileId}`} />
      </td>
    </tr>
  </div>
);

ProfileAudio.propTypes = {
  audio: PropTypes.object.isRequired,
  playAudio: PropTypes.func.isRequired,
};

export default connect(null, { playAudio })(ProfileAudio);
